    /// <summary>
    /// Id of contact to edit
    /// </summary>
    [Parameter]
    public string Id { get; set; }

    // context database access
    private ContactDbContext context { get; set; }

    /// Contact object being edited
    private Contact contact { get; set; }


    // indicates that app is busy
    private bool Busy;

    // true if an unexpected error occurs during update
    private bool Error;

    // Error message
    private string ErrorMessage = string.Empty;

    // true if modal is open
    private bool isModalOpen = false;

    // opens delete confirmation modal
    void openModal()
    {
        isModalOpen = true;
    }

    // closes delete confirmation modal
    void closeModal()
    {
        isModalOpen = false;
    }

    // load contact data
    protected override async Task OnInitializedAsync()
    {
        Busy = true;

        try
        {
            context = DbFactory.CreateDbContext();
            contact = await context.Contacts
                .SingleOrDefaultAsync(c => c.Id == Guid.Parse(Id));
        }
        finally
        {
            Busy = false;
        }

        await base.OnInitializedAsync();
    }
    

    // Result of form validation
    private async Task ValidationResultAsync(bool success)
    {
        if (Busy)
        {
            return;
        }

        if (!success)
        {
            // did not pass validation, but no errors
            Error = false;
            return;
        }

        Busy = true; // async
        try
        {
            // update contact if validation passes
            await context.SaveChangesAsync();
            Nav.NavigateTo("");
        }   
        catch (Exception ex)
        {
             // unknown exception thrown
            Error = true;
            ErrorMessage = ex.Message;
            Busy = false;
        }
    }

    // delete contact record from database
    private async void DeleteContact()
    {
         Busy = true; // async
        try
        {
           context.Contacts.Remove(contact);
           await context.SaveChangesAsync();
           Busy = false;
           Nav.NavigateTo("");
        }
        catch (Exception ex)
        {
             // unknown exception thrown
            Error = true;
            ErrorMessage = ex.Message;
            Busy = false;
        }

       
    }

    // returns user to home screen
    private void Cancel()
    {
        Busy = true;
        Nav.NavigateTo("");
    }


